function time(){date=new Date,ms=date.getMilliseconds(),s=date.getSeconds(),m=date.getMinutes()+(ms/1e3+s)/60,h=date.getHours()+m/60,second.style.WebkitTransform="rotate("+6*s+"deg)",minute.style.WebkitTransform="rotate("+6*m+"deg)",hour.style.WebkitTransform="rotate("+30*h+"deg)",second.style.msTransform="rotate("+6*s+"deg)",minute.style.msTransform="rotate("+6*m+"deg)",hour.style.msTransform="rotate("+30*h+"deg)",ampm.children[0].innerHTML=h>=0&&h<5?"凌 晨":h<8?"早 晨":h<11?"上 午":h<14?"中 午":h<17?"下 午":h<19?"傍 晚":h<22?"晚 上":"深 夜"}var date,ms=0,s=0,m=0,h=0,second=document.getElementById("secondhand"),minute=document.getElementById("minutehand"),hour=document.getElementById("hourhand"),ampm=document.getElementById("ampm");window.setInterval("time()",100);