function change(){for(var t=0;t<json.length;t++)$(".rotate img:eq("+t+")").animate({width:json[t].width,top:json[t].top,left:json[t].left,opacity:json[t].opacity/100,zIndex:json[t].zIndex},400,function(){flag=!0}).css({flter:"alpha(opacity="+json[t].opacity+")"})}var json=[{width:400,top:20,left:50,opacity:20,zIndex:2},{width:600,top:70,left:0,opacity:80,zIndex:3},{width:800,top:100,left:200,opacity:100,zIndex:4},{width:600,top:70,left:600,opacity:80,zIndex:3},{width:400,top:20,left:750,opacity:20,zIndex:2}],flag;change(),$(".prev").click(function(){flag&&(json.unshift(json.pop()),change(),flag=!1)}),$(".next").click(function(){flag&&(json.push(json.shift()),change(),flag=!1)});